}

// Now try to break it:
const brokenUser = {
  email: 'test@example.com',
  firstName: 'Test',
  lastName: 'User',
  // Missing required properties!
};

updateUser(brokenUser);
// ‚ùå ERROR: Property 'id' is missing in type...
